import React, { useState } from 'react'

const Header =(props)=>{
  return(
    <header>
      <h1>
        <a href='/' onClick={(event)=>{
          event.preventDefault();
          props.onChangeMode();
        }}>{props.title}</a>
      </h1>
    </header>
  )
}
const Nav =(props)=> {
  const lis = [];
  props.topics.map((item) => lis.push(<li key={item.id}><a id={item.id} href={`/read/${item.id}`} onClick={(event)=>{
    event.preventDefault();
    props.onChangeMode(Number(event.target.id));
  }}>{item.title}</a></li>));
  return(
    <nav>
    <ol>
      {lis}
    </ol>
  </nav>
  )
}
const Article =(props)=> {
  return(
    <article>
      <h2>{props.title}</h2>
      {props.body}
    </article>
  )
}
const Create =()=> {
  return <article>
    <h2></h2>
  </article>
}
function App() {
  const [mode, setMode] = useState('');
  const [id, setId] = useState(null);
  const topics = [
    {id:1, title:"html", body:"html is ..."},
    {id:2, title:"css", body:"css is ..."},
    {id:3, title:"javascript", body:"javascript is ..."},
  ]
  let content = null;
  if(mode === "WELCOME"){
    content = <Article title="Welcome" body="Hello, WEB"></Article>
  } else if (mode === 'READ'){
    topics.map(item => {      
      if(item.id === id) {
        content = <Article title={item.title} body={item.body}></Article>
      }
    })
  } else if (mode === 'CREATE'){
    <Create></Create>
  }
  return (
    <div>
      <Header title="WEB" onChangeMode={()=>{
        setMode("WELCOME");
      }}></Header>
      <Nav topics={topics} onChangeMode={(_id)=>{
         setMode("READ");
         setId(_id)
      }}></Nav>
      {content}
      <a href='/create' onClick={event => {
        event.preventDefault();
        setMode('CREATE')
      }}>Create</a>
    </div>
  )
}

export default App
