import React from 'react'

function App() {
  const mode = "WELCOME";
  const Header =(props)=>{
    return(
      <header>
        <h1>
          <a href='/' onClick={(event)=>{
            event.preventDefault();
            props.onChangeMode();
          }}>{props.title}</a>
        </h1>
      </header>
    )
  }
  const Nav =(props)=> {
    const lis = [];
    props.topics.map((item) => lis.push(<li key={item.id}><a id={item.id} href={`/read/${item.id}`} onClick={(event)=>{
      event.preventDefault();
      props.onChangeMode(event.target.id);
    }}>{item.title}</a></li>));
    return(
      <nav>
      <ol>
        {lis}
      </ol>
    </nav>
    )
  }
  const Article =(props)=> {
    return(
      <article>
        <h2>{props.title}</h2>
        {props.body}
      </article>
    )
  }

