import React, { useState } from 'react'

const Header =(props)=>{
  return(
    <header>
      <h1>
        <a href='/' onClick={(event)=>{
          event.preventDefault();
          props.onChangeMode();
        }}>{props.title}</a>
      </h1>
    </header>
  )
}
const Nav =(props)=> {
  const lis = [];
  props.topics.map((item) => lis.push(<li key={item.id}><a id={item.id} href={`/read/${item.id}`} onClick={(event)=>{
    event.preventDefault();
    props.onChangeMode(Number(event.target.id));
  }}>{item.title}</a></li>));
  return(
    <nav>
    <ol>
      {lis}
    </ol>
  </nav>
  )
}
const Article =(props)=> {
  return(
    <article>
      <h2>{props.title}</h2>
      {props.body}
    </article>
  )
}
const Create =(props)=>{
  return(
    <article>
      <h2>Create</h2>
      <form onSubmit={event=> {
        event.preventDefault();
        const title = event.target.title.value;
        const body = event.target.body.value;
        props.onCreate(title, body);
      }}>
        <p><input type='text' name="title" placeholder='title'></input></p>
        <p><textarea name='body' placeholder='body'></textarea></p>
        <p><input type='submit' value="Create"></input></p>
      </form>
    </article>
  )
}

function App() {
  const [mode, setMode] = useState('');
  const [id, setId] = useState(null);
  const [newxId, setNextId] = useState(4);
  const [topics, setTopics] = useState=([
    {id:1, title:"html", body:"html is ..."},
    {id:2, title:"css", body:"css is ..."},
    {id:3, title:"javascript", body:"javascript is ..."},
  ]);
  let content = null;
  if(mode === "WELCOME"){
    content = <Article title="Welcome" body="Hello, WEB"></Article>
  } else if (mode === 'READ'){
    topics.map(item => item.id === id ? content = <Article title={item.title} body={item.body}></Article> : null );
  } else if (mode === 'CREATE'){
    content = <Create onCreate={(_title, _body)=>{
      const newTopic = {id:newxId, title:_title, body:_body}
      const newTopics = [...topics]
    }}></Create>
  }
  return (
    <div>
      <Header title="WEB" onChangeMode={()=>{
        setMode("WELCOME");
      }}></Header>
      <Nav topics={topics} onChangeMode={(_id)=>{
         setMode("READ");
         setId(_id)
      }}></Nav>
      {content}
      <a href='/create' onClick={event => {
        event.preventDefault();
        setMode("CREATE");
      }}>Create</a>
    </div>
  )
}

export default App
